  const [pokemon, setPokemon] = useState([]);

  const fetchPokemonWithAxios =()=>{
    axios.get("https://pokeapi.co/api/v2/pokemon?limit=100000&offset=0")
      .then((response) =>{
        setPokemon(response.data);
    })
  }

