import React, {useState} from 'react'
import DisplayPokemon from './DisplayPokemon'
import axios from 'axios'

const FetchPokemon = () =>{
    const [pokemon, setPokemon] = useState()

    const fetchPokemonWithThen =() =>{
    fetch("https://pokeapi.co/api/v2/pokemon/bulbasaur")
    .then(response =>{
        console.log(response)
        return response.json()
    })
    .then(jsonResponse => {
        console.log(jsonResponse)
        setPokemon(jsonResponse)
    })
    .catch(err=>console.log(err))
}

    const fetchPokemonWithAxios =()=>{
        axios.get("https://pokeapi.co/api/v2/pokemon/")
            .then(response=>{
                setPokemon(response.data)
            })
    }
return(
<div>
    <button onClick={fetchPokemonWithThen}>Fetch Pokemon</button>
    <button onClick={fetchPokemonWithAxios}>Fetch Pokemon with Axios</button>

    {
        pokemon?
            <DisplayPokemon pokemon={pokemon} />:
            <h1>Your Pokemon Here</h1>

    }

</div>

)}

export default FetchPokemon
